  "img3": "images/schattenmenschen.jpg",
  "img3_alt": "Menschenschatten - Photo von Michaël Verry",
  "img3_placeholder": "data:image/jpeg;base64,",
  "unterschrift3": "\"Menschenschatten - Schattenmenschen\", Kartoffelkeller, Photo Michaël Verry",
  "size4": "width:675px; height:839px;margin: 0 112px;",
  "img4": "images/torso.jpg",
  "img4_alt": "Schmerz-Schuld, Photo von Michaël Verry",
  "img4_placeholder": "data:image/jpeg;base64,",
  "unterschrift4": "\"Schmerz-Schuld\", Kartoffelkeller, Photo Michaël Verry",
  "size5": "width:675px; height:950px; margin: 0 112px;",
  "img5": "images/ueberwindung.jpg",
  "img5_alt": "Ueberwindung - Photo von Michaël Verry",
